const { fork } = require('child_process');

const forkChild = () => {
  const child = fork('log.js');
  child.on('exit', () => {
    setTimeout(() => {
      forkChild();
    }, 1000);
  });
};

forkChild();

// log.js
const fs = require('fs');
const path = require('path');

fs.open(path.resolve(__dirname, 'log.txt'), 'w', function (err, fd) {
  setInterval(() => {
    fs.write(fd, process.pid + '\\n', function () {});
  }, 2000);
});
