  //\u83B7\u53D6\u5806\u7684\u5927\u5C0F
  getSize() {
    return this.data.length - 1;
  }
  //\u5411\u4E0A\u5806\u5316
  __shiftUp(k) {
    while (k > 1 && this.data[k] > this.data[parseInt(k / 2)]) {
      this.__swap(k, parseInt(k / 2));
      k = parseInt(k / 2);
    }
  }
  //\u4EA4\u6362\u5143\u7D20
