  let length = s.length;
  length % 2 === 1 ? (length = length - 1) : '';
  while (length !== 0) {
    for (let i = 0; i <= s.length - length; i++) {
      let str = s.substr(i, length);
      const flag = isValid(str);
      if (flag) {
        return length;
      }
    }
    length -= 2;
  }
  return 0;
