__dfsVisit(v, color, cb) {
  color[v] = "grey";
  cb && cb(v);
  const neighbors = this.adjList.get(v);
  neighbors.forEach((neighbor) => {
    color[neighbor] === "white" && this.__dfsVisit(neighbor, color, cb);
  });
  color[v] = "black";
}
